/**
 * TailwindCSSの`justify-content`に対応するクラス名
 * @see https://tailwindcss.com/docs/justify-content
 */
export type JustifyContentClass =
  | "justify-start"
  | "justify-end"
  | "justify-center"
  | "justify-between"
  | "justify-around"
  | "justify-evenly"
  | "justify-stretch"
  | "justify-start-safe"
  | "justify-end-safe"
  | "justify-center-safe";
